bf6 = 0.2005166131962391 * scaling_factor

intervals <- data.frame(
  xmin = c(-Inf, -168.2, -165.3, -161.5, -154.8, -149.2, -145, -139.8, -132.6, -125.77, -121.4, -113, -100.5),
