p_vals <- c(0.01, 0.02, 0.03)
q_factors <- c(1/10, 1/5, 1/2)

get_vec <- function(var, p, q, idx) {
  p_str <- formatC(p, format = "f", digits = 2)
  q_str <- formatC(q, format = "f", digits = 3)
  var_name <- paste0(var, "_p", p_str, "q", q_str, "_", idx)
  get(var_name, envir = .GlobalEnv)
}

tiff("Global_sp_syn.tiff", width = 2048, height = 1024, res = 300)

par(mfrow = c(3, 3),
    mar = c(2, 2, 1.5, 1.5),
    oma = c(3, 3, 2, 2),
    mgp = c(1.5, 0.4, 0))

for(i in seq_along(p_vals)) {
  p <- p_vals[i]
  
  for(j in seq_along(q_factors)) {
    q <- p * q_factors[j]
    
    all_times <- c()
    all_rates <- c()
    all_minHPD <- c()
    all_maxHPD <- c()
    for(idx in 1:5) {
      t <- get_vec("time", p, q, idx)
      r <- get_vec("rate", p, q, idx)
      mn <- get_vec("minHPD", p, q, idx)
      mx <- get_vec("maxHPD", p, q, idx)
      
      all_times <- c(all_times, t)
      all_rates <- c(all_rates, r)
      all_minHPD <- c(all_minHPD, mn)
      all_maxHPD <- c(all_maxHPD, mx)
    }
    
    xlim <- range(all_times)
    ylim <- range(c(all_minHPD, all_maxHPD))
    
    plot(NA, NA, xlim = xlim, ylim = ylim,
         xlab = "", ylab = "", xaxt = "n", yaxt = "n",
         main = "", bty = "n")
    
    for(idx in 1:5) {
      t <- get_vec("time", p, q, idx)
      r <- get_vec("rate", p, q, idx)
      mn <- get_vec("minHPD", p, q, idx)
      mx <- get_vec("maxHPD", p, q, idx)
      
      polygon(c(t, rev(t)), c(mn, rev(mx)),
              col = rgb(0, 0, 1, 0.2), border = NA)
      
      lines(t, r, col = rgb(0, 0, 1, 0.8), lwd = 0.8)
    }
    
    xticks <- seq(floor(xlim[1]/20)*20, ceiling(xlim[2]/20)*20, by = 20)
    xticklabels <- gsub("-", "", xticks)
    
    axis(1, at = xticks, labels = xticklabels)
    yticks <- seq(floor(ylim[1]/0.5)*0.5, ceiling(ylim[2]/0.5)*0.5, by = 0.5)
    axis(2, at = yticks, las = 1)
    
    q_labels <- c("q = p/10", "q = p/5", "q = p/2")
    
    if(i == 1) {
      mtext(q_labels[j], side = 3, line = 1, at = mean(xlim), cex = 0.8)
    }
    
    if(j == length(q_factors)) {
      mtext(paste0("p = ", formatC(p, digits=2)), side = 4, line = 2, at = mean(ylim), cex = 0.8)
    }
  }
}

